SELECT v.FINANCIAL_PERIOD AS "Отчетный Период", v.BILL_MONTH AS "Bill Month", v.IMEI AS "IMEI", v.CONTRACT_ID AS "Contract ID", NVL(v.FEE_ACTIVATION_FEE, 0) AS "Activation Fee", NVL(v.FEE_ADVANCE_CHARGE, 0) AS "Advance Charge", NVL(v.FEE_ADVANCE_CHARGE_PREVIOUS_MONTH, 0) AS "Advance Charge Previous Month", NVL(v.FEE_CREDIT, 0) AS "Credit", NVL(v.FEE_CREDITED, 0) AS "Credited", NVL(v.FEE_PRORATED, 0) AS "Prorated" FROM V_CONSOLIDATED_REPORT_WITH_BILLING v WHERE 1=1 AND v.BILL_MONTH = '2025-09' AND v.IMEI = '300234069907470' ORDER BY v.BILL_MONTH DESC;

